//******************************************
//****** DetailsPage *************************
//******************************************

import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';

const TableDiv = styled.table`
margin-top:100px;
background-color:#e7f0c3;
margin-left:100px;
margin-right:200px;
padding-top:30px;
padding-bottom:30px;
padding-left:30px;
`;

const DetailsPage = (props) => {
    const [data, setData] = useState([]);

    useEffect(() => {
        if (props.location.state.key) {
            fetch(`https://jsonplaceholder.typicode.com/users?id=${props.location.state.key}`, {
                method: "GET",
            }).then((response) => {
                return response.json()
            }).then((body) => {
                console.log(body)
                setData(body)
            })
        }
    }, [])


    return (

        <div>
            <TableDiv >
                <thead>
                    <tr>
                        <th>
                            Id
                                        </th>
                        <th>
                            Name
                                        </th>
                        <th>
                            UserName
                                        </th>
                        <th>
                            Phone
                                        </th>
                        <th>
                            Website
                                     </th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((data) => {
                        return (
                            <tr>
                                <td>{data.id}</td>
                                <td>{data.name}</td>
                                <td>{data.username}</td>
                                <td>{data.phone}</td>
                                <td>{data.website}</td>
                            </tr>
                        )
                    })}
                </tbody>
            </TableDiv>

        </div>

    );
};

DetailsPage.propTypes = {};

export default DetailsPage;
